import React,{useState,useEffect} from 'react'
import styled from 'styled-components'
import {Link} from 'react-router-dom'
import Logo from '../assets/logo.png'
import {ToastContainer,toast} from 'react-toastify';

import 'react-toastify/dist/ReactToastify.css';
function Register() {

  const [values,setValues]=useState({
    username:"",email:"",password:"",confirmPassword:"",
  })

  const handleSubmit=(event) => {
    event.preventDefault(); 
    handleValidation();
  }

  const toastOptions={
    position:'top-center',
    autoClose:5000,
    draggable:true,
    pauseOnHover:true,
    theme:'dark',
  }
  const handleValidation=() => {
    const {username,email,password,confirmPassword}=values;
    if(password!=confirmPassword) {
      toast.error("Passwoed and confirm password not matches",toastOptions);//display amazing effect error message 
      return false;
    }
    else if(username.length<3){
      toast.error("Username Should be greater than 3 characters",{ toastOptions});//display amazing effect error message 
      return false;
    }
    else if(password.length<8){
      toast.error("Password be greter than or equals to 8 characters",{ toastOptions});//display amazing effect error message 
      return false;
    }
